import { Button, Dropdown, Space, Col, Row } from 'antd';
import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { CaretDownOutlined } from '@ant-design/icons';


export default function Content() {
    // const dispatch = useDispatch()
    // const { listStore } = useSelector(state => state.storeReducer)

    const onMenuClick = (e) => {
        console.log('click', e);
    };
    const items = [
        {
            key: '1',
            label: '1st item',
        },
        {
            key: '2',
            label: '2nd item',
        },
        {
            key: '3',
            label: '3rd item',
        },
    ];

    return (
        <div style={{ width: '100%', height: '713px', overflowY: 'scroll' }}>
            <p style={{ marginTop: '40px', fontSize: '40px', color: '#203259', fontWeight: '500' }}>Store {JSON.parse(localStorage.getItem('store')).id} Menu</p>
            <Row style={{ marginTop: '30px' }}>
                <Col span={2}>

                </Col>
                <Col span={10} style={{ textAlign: 'left' }}>
                    <button style={{ backgroundColor: '#203259', width: '140px', height: '40px', borderRadius: '5px', color: 'white', fontSize: '18px' }}>Filter</button>
                </Col>
                <Col span={5}>

                </Col>
                <Col span={7} style={{ textAlign: 'left', paddingLeft: '60px' }}>
                    <p style={{ fontSize: '17px', color: '#203259', fontWeight: '500' }}>Sort By <Button style={{borderColor: '#203259', marginLeft: '15px', height: '30px', width: '110px'}}>
                        Name <CaretDownOutlined />
                    </Button></p>
                </Col>
            </Row>
        </div>
    )
}
