import { GET_ALL_STORE } from "../type/StoreType"

const store = {
    listStore: [],
    listStoreAndProduct: []
}

const storeReducer = (state = store, action) => {
    switch (action.type) {
        case GET_ALL_STORE: {
            let arrayStore = action.data
            state.listStore = [...arrayStore]
            console.log(state.listStore)
            return { ...state }
        }
        default: {
            return { ...state }
        }
    }
}

export default storeReducer
