import { Button, Dropdown, Col, Row, Card } from 'antd';
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { CaretDownOutlined } from '@ant-design/icons';


export default function Content() {
    // const dispatch = useDispatch()
    // const { listStore } = useSelector(state => state.storeReducer)
    const [filterName, setFilterName] = useState({ name: 'Name' })

    const onMenuClick = (e) => {
        if (e.key == 1) {
            setFilterName({ ...filterName, name: 'Tên tăng dần' })
        } else if (e.key == 2) {
            setFilterName({ ...filterName, name: 'Tên giảm dần' })
        } else if (e.key == 3) {
            setFilterName({ ...filterName, name: 'Giá tăng dần' })
        } else if (e.key == 4) {
            setFilterName({ ...filterName, name: 'Giá giảm dần' })
        }
    };
    const items = [
        {
            key: '1',
            label: 'Tên tăng dần',
        },
        {
            key: '2',
            label: 'Tên giảm dần',
        },
        {
            key: '3',
            label: 'Giá tăng dần',
        },
        {
            key: '4',
            label: 'Giá giảm dần',
        },
    ];

    const renderItem = () => {
        return <Card
        hoverable
        style={{
            width: '250',
            boxShadow: '1px 1px 1px grey'
        }}
    >
        <p>MT-01</p>
    </Card>
    }

    return (
        <div style={{ width: '100%', height: '713px', overflowY: 'scroll', backgroundColor: '#f5f5f5' }}>
            <p style={{ marginTop: '40px', fontSize: '40px', color: '#203259', fontWeight: '500' }}>Store {JSON.parse(localStorage.getItem('store')).id} Menu</p>
            <Row style={{ marginTop: '30px' }}>
                <Col span={2}>

                </Col>
                <Col span={10} style={{ textAlign: 'left' }}>
                    <button style={{ backgroundColor: '#203259', width: '140px', height: '40px', borderRadius: '5px', color: 'white', fontSize: '18px' }}>Filter</button>
                </Col>
                <Col span={5}>

                </Col>
                <Col span={7} style={{ textAlign: 'left', paddingLeft: '40px' }}>
                    <p style={{ fontSize: '17px', color: '#203259', fontWeight: '500' }}>Sort By <Dropdown
                        menu={{
                            items,
                            onClick: onMenuClick,
                        }}
                        placement="bottomLeft"
                    >
                        <Button style={{ width: '130px', height: '38px', marginLeft: '10px', textAlign: 'center' }}>{filterName.name} <CaretDownOutlined /></Button>
                    </Dropdown></p>
                </Col>
            </Row>
            <Row>
                {renderItem()}
            </Row>
        </div>
    )
}
