import { Button, Dropdown, Col, Row, Card } from 'antd';
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { CaretDownOutlined } from '@ant-design/icons';
import { GET_ALL_STORE_AND_PRODUCT_SAGA } from '../redux/type/StoreType';


export default function Content() {
    const dispatch = useDispatch()
    const { listStoreAndProduct, status } = useSelector(state => state.storeReducer)
    const [filterName, setFilterName] = useState({ name: 'Name' })

    useEffect(() => {
        dispatch({
            type: GET_ALL_STORE_AND_PRODUCT_SAGA
        })
    }, [])

    useEffect(() => {
    }, [JSON.parse(localStorage.getItem('store'))])

    const onMenuClick = (e) => {
        if (e.key == 1) {
            setFilterName({ ...filterName, name: 'Tên tăng dần' })
        } else if (e.key == 2) {
            setFilterName({ ...filterName, name: 'Tên giảm dần' })
        } else if (e.key == 3) {
            setFilterName({ ...filterName, name: 'Giá tăng dần' })
        } else if (e.key == 4) {
            setFilterName({ ...filterName, name: 'Giá giảm dần' })
        }
    };
    const items = [
        {
            key: '1',
            label: 'Tên tăng dần',
        },
        {
            key: '2',
            label: 'Tên giảm dần',
        },
        {
            key: '3',
            label: 'Giá tăng dần',
        },
        {
            key: '4',
            label: 'Giá giảm dần',
        },
    ];

    const renderItem = () => {
        return listStoreAndProduct.map(item => {
            if (item.idShop == JSON.parse(localStorage.getItem('store')).id) {
                return <Col span={8} style={{ marginTop: '25px', marginBottom: '25px' }}>
                    <Card
                        hoverable
                        style={{
                            width: '300px',
                            boxShadow: '1px 1px 1px grey',
                            textAlign: 'left',
                            paddingLeft: '10px',
                            margin: '0 auto'
                        }}
                    >
                        <p style={{ fontSize: '20px', color: '#203259', paddingTop: '0px' }}>MT-01</p>
                        <p style={{ fontSize: '20px', color: '#203259', marginTop: '-10px', paddingBottom: '10px', fontWeight: '500', borderBlockEnd: '3px #203259 solid' }}>{item.product.name}</p>
                        <p style={{ fontSize: '20px', color: '#203259', paddingTop: '0px', fontWeight: '500' }}>Toppings: </p>
                    </Card>
                </Col>
            }
        })
    }

    return (
        <div style={{ width: '100%', height: '713px', overflowY: 'scroll', backgroundColor: '#f5f5f5' }}>
            <p style={{ marginTop: '40px', fontSize: '40px', color: '#203259', fontWeight: '500' }}>Store {JSON.parse(localStorage.getItem('store')).id} Menu</p>
            <Row style={{ marginTop: '30px' }}>
                <Col span={1}>

                </Col>
                <Col span={11} style={{ textAlign: 'left' }}>
                    <button style={{ backgroundColor: '#203259', width: '140px', height: '40px', borderRadius: '5px', color: 'white', fontSize: '18px' }}>Filter</button>
                </Col>
                <Col span={6}>

                </Col>
                <Col span={6} style={{ textAlign: 'left', paddingLeft: '40px' }}>
                    <p style={{ fontSize: '17px', color: '#203259', fontWeight: '500', paddingLeft: '7px' }}>Sort By <Dropdown
                        menu={{
                            items,
                            onClick: onMenuClick,
                        }}
                        placement="bottomLeft"
                    >
                        <Button style={{ width: '130px', height: '38px', marginLeft: '10px', textAlign: 'center' }}>{filterName.name} <CaretDownOutlined /></Button>
                    </Dropdown></p>
                </Col>
            </Row>
            <Row style={{ marginTop: '40px' }}>
                {renderItem()}
            </Row>
        </div>
    )
}
